// src/Login.js

import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function Login() {
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleLoginSubmit = async (e) => {
    e.preventDefault();

    // Implement login logic here
    try {
      const response = await fetch('http://127.0.0.1:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: loginEmail,
          password: loginPassword,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        // Save the token to localStorage
        localStorage.setItem('token', data.token);
        // Redirect to chat page
        navigate('/chat');
      } else {
        setErrorMessage(data.error || 'Login failed');
      }
    } catch (error) {
      setErrorMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-[#0e0e0e]">
      <div className="relative w-96 max-w-full">
        <div className="bg-[#1e1e1e] rounded-lg p-8 shadow-lg">
          <h2 className="text-2xl text-center mb-6">Sign In</h2>
          <form onSubmit={handleLoginSubmit}>
            {errorMessage && (
              <div className="mb-4 text-red-500 text-center">
                {errorMessage}
              </div>
            )}
            <div className="mb-4">
              <input
                type="email"
                placeholder="Email"
                value={loginEmail}
                onChange={(e) => setLoginEmail(e.target.value)}
                required
                className="w-full px-4 py-2 rounded bg-gray-800 text-white placeholder-gray-400 focus:outline-none"
              />
            </div>
            <div className="mb-6">
              <input
                type="password"
                placeholder="Password"
                value={loginPassword}
                onChange={(e) => setLoginPassword(e.target.value)}
                required
                className="w-full px-4 py-2 rounded bg-gray-800 text-white placeholder-gray-400 focus:outline-none"
              />
            </div>
            <button
              type="submit"
              className="w-full py-2 mb-4 bg-[#0693e3] hover:bg-blue-600 rounded text-white font-semibold transition-colors"
            >
              Login
            </button>
            <p className="text-center">
              Don't have an account?{' '}
              <Link
                to="/register"
                className="text-[#0693e3] hover:underline cursor-pointer"
              >
                Register
              </Link>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
