// src/App.js
import React, { useState, useEffect, useRef } from 'react';
import './App.css';

// CopyButton Component with Fallback Copy Method and Improved Accessibility
function CopyButton({ text }) {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    if (!navigator.clipboard) {
      // Fallback using textarea if Clipboard API is not available
      const textArea = document.createElement("textarea");
      textArea.value = text;
      document.body.appendChild(textArea);
      textArea.focus();
      textArea.select();
      try {
        document.execCommand('copy');
        setCopied(true);
        setTimeout(() => setCopied(false), 1000);
      } catch (err) {
        alert('Failed to copy text.');
      }
      document.body.removeChild(textArea);
      return;
    }

    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 1000);
    } catch (error) {
      alert('Failed to copy text. Your browser may not support this feature.');
    }
  };

  return (
    <button
      className="copy-button"
      aria-label={copied ? 'Text copied' : 'Copy text'}
      data-tooltip={copied ? 'Copied' : 'Copy'}
      onClick={handleCopy}
    >
      {copied ? (
        // Checkmark Icon SVG
        <svg fill="currentColor" viewBox="0 0 20 20">
          <path d="M16.707 5.293a1 1 0 00-1.414 0L7.5 13.086l-2.793-2.793a1 1 0 00-1.414 1.414l3.5 3.5a1 1 0 001.414 0l8-8a1 1 0 000-1.414z"></path>
        </svg>
      ) : (
        // Copy Icon SVG
        <svg fill="currentColor" viewBox="0 0 20 20">
          <path d="M8 2a2 2 0 00-2 2v2h2V4h8v8h-2v2h2a 2 2 0 002-2V4a 2 2 0 00-2-2H8z"></path>
          <path d="M2 8a2 2 0 012-2h8a 2 2 0 012 2v8a 2 2 0 01-2 2H4a 2 2 0 01-2-2V8z"></path>
        </svg>
      )}
    </button>
  );
}


function App() {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [botTyping, setBotTyping] = useState(false);
  const [userScrolled, setUserScrolled] = useState(false);
  const [showGoDownButton, setShowGoDownButton] = useState(false);
  const lastScrollTop = useRef(0);
  const chatBodyRef = useRef(null);
  const [profileMenuOpen, setProfileMenuOpen] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const profileMenuRef = useRef(null);

  useEffect(() => {
    sendWelcomeMessage();
  }, []);

  useEffect(() => {
    if (!userScrolled) {
      scrollToBottom();
    }
  }, [messages, userScrolled]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        profileMenuRef.current &&
        !profileMenuRef.current.contains(event.target) &&
        event.target.nodeName !== 'IMG'
      ) {
        setProfileMenuOpen(false);
      }
    };

    if (profileMenuOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [profileMenuOpen]);

  const sendWelcomeMessage = () => {
    const welcomeMessage =
      'Hello! And welcome to AskNAU. How can I assist you today?';
    appendMessage(welcomeMessage, false);
  };

  const appendMessage = (content, isUser = true) => {
    if (isUser) {
      setMessages((prevMessages) => [...prevMessages, { content, isUser }]);
    } else {
      typeBotMessage(content);
    }
  };

  // Function to type out the bot's message word by word
  const typeBotMessage = (fullContent) => {
    setBotTyping(true);
    const words = fullContent.split(' ');
    let i = 0;
    const speed = 50; // Adjust the speed as needed

    // Add initial empty message
    setMessages((prevMessages) => [
      ...prevMessages,
      { content: '', isUser: false },
    ]);

    const typingInterval = setInterval(() => {
      if (i < words.length) {
        setMessages((prevMessages) => {
          const lastMessage = prevMessages[prevMessages.length - 1];
          const newContent =
            (lastMessage.content ? lastMessage.content + ' ' : '') + words[i];
          const updatedMessages = [
            ...prevMessages.slice(0, -1),
            { ...lastMessage, content: newContent },
          ];
          return updatedMessages;
        });
        i++;
      } else {
        clearInterval(typingInterval);
        setBotTyping(false);
      }
    }, speed);
  };

  const sendMessage = async (message = null) => {
    if (botTyping) return;

    const msg = message || inputValue;
    if (msg.trim() === '') return;

    // Check if the message exceeds 2000 characters
    if (msg.length > 2000) {
      setErrorMessage('Message cannot exceed 2000 characters.');
      return;
    } else {
      setErrorMessage(''); // Clear any existing error messages
    }

    appendMessage(msg, true);
    if (!message) {
      setInputValue('');
    }

    setBotTyping(true);

    try {
      // Send the user's question to the backend
      const response = await fetch('http://127.0.0.1:5000/ask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question: msg }),
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.statusText}`);
      }

      const data = await response.json();

      if (data.error) {
        appendMessage(`Error here ->: ${data.error}`, false);
      } else {
        // Use typeBotMessage to display the bot's response word by word
        typeBotMessage(data.answer);
      }
    } catch (error) {
      appendMessage(`Error this ->: ${error.message}`, false);
    } finally {
      // setBotTyping(false); // This is now handled in typeBotMessage
    }
  };

  const scrollToBottom = () => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTo({
        top: chatBodyRef.current.scrollHeight,
        behavior: 'smooth',
      });
      setUserScrolled(false);
    }
  };

  const handleChatBodyScroll = () => {
    const chatBody = chatBodyRef.current;
    const currentScrollTop = chatBody.scrollTop;
    const isAtBottom =
      Math.abs(
        chatBody.scrollHeight - chatBody.scrollTop - chatBody.clientHeight
      ) < 1;

    if (currentScrollTop > lastScrollTop.current && !isAtBottom) {
      if (userScrolled) {
        setShowGoDownButton(true);
      }
    } else if (isAtBottom) {
      setShowGoDownButton(false);
      setUserScrolled(false);
    } else {
      setUserScrolled(true);
      setShowGoDownButton(false);
    }
    lastScrollTop.current = currentScrollTop;
  };

  const toggleProfileMenu = () => {
    setProfileMenuOpen(!profileMenuOpen);
  };

  return (
    <div className="app-container text-gray-300 flex items-center justify-center h-screen">
      <div className="w-full h-full flex flex-col max-w-4xl mx-auto">
        {/* Chat Header */}
        <div className="chat-header p-4 flex items-center justify-between">
          <div className="flex items-center">
            <span className="text-2xl font-bold">AskNAU</span>
            <span
              style={{ color: 'rgba(6,147,227,1)' }}
              className="text-x font-normal ml-2"
            >
              (North American University AI)
            </span>
          </div>
          {/* Profile Picture and Menu */}
          <div className="relative">
            <img
              src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png"
              alt="User profile"
              className="w-8 h-8 rounded-full cursor-pointer"
              onClick={toggleProfileMenu}
            />
            {profileMenuOpen && (
              <div
                id="profile-menu" ref={profileMenuRef} className="absolute right-0 mt-2 w-48 rounded-md shadow-lg">
                <a href="#" className="block px-4 py-2 hover:bg-gray-700">Profile</a>
                <a href="#" className="block px-4 py-2 hover:bg-gray-700">About</a>

              </div>
            )}
          </div>
        </div>
        {/* Chat Body */}
        <div
          className="chat-body flex flex-col p-4 space-y-4 overflow-y-auto"
          ref={chatBodyRef}
          onScroll={handleChatBodyScroll}
        >
          {messages.map((message, index) => (
            <div
              key={index}
              className={`message ${
                message.isUser ? 'user-message' : 'bot-message'
              }`}
            >
              {message.isUser ? (
                message.content
              ) : (
                <div className="message-wrapper">
                  <div className="message-content">{message.content}</div>
                  <CopyButton text={message.content} />
                </div>
              )}
            </div>
          ))}
        </div>
        {/* Chat Footer */}
        <div className="chat-footer p-4 flex flex-col items-center space-y-2">
          {errorMessage && (
            <div className="error-message text-red-600 text-sm">
              {errorMessage}
            </div>
          )}
          <div className="input-container flex-1 w-full flex items-center">
            <input
              type="text"
              value={inputValue}
              onChange={(e) => {
                if (e.target.value.length <= 2000) {
                  setInputValue(e.target.value);
                  setErrorMessage(''); // Clear any existing error messages
                } else {
                  setErrorMessage('Message cannot exceed 2000 characters.');
                }
              }}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  e.preventDefault();
                  sendMessage();
                }
              }}
              placeholder="Message..."
              autoComplete="off"
              className="flex-grow p-2 rounded-l-md bg-gray-800 text-white"
            />
            <button
              onClick={() => sendMessage()}
              className="p-2 bg-blue-600 rounded-r-md"
            >
              {/* Arrow SVG */}
              <svg
                className="w-6 h-6 text-white"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M9 5l7 7-7 7"
                ></path>
              </svg>
            </button>
          </div>

          {/* Disclaimer */}
          <div className="disclaimer">@ AskNAU - 2024</div>
        </div>
        {/* Scroll to Bottom Button */}
        {showGoDownButton && (
          <button className="go-down-button" onClick={scrollToBottom}>
            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M19 9l-7 7-7-7"
              ></path>
            </svg>
          </button>
        )}
      </div>
    </div>
  );
}




export default App;
