// src/Chat.js
import React, { useState, useEffect, useRef, useCallback } from 'react';
import './Chat.css';
import { useNavigate, useLocation } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faPlay, faRightFromBracket, faClockRotateLeft, faPlus } from '@fortawesome/free-solid-svg-icons';

function CopyButton({ text }) {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 1000);
    } catch {
      alert('Failed to copy text.');
    }
  };

  return (
    <button
      className="copy-button"
      aria-label={copied ? 'Text copied' : 'Copy text'}
      onClick={handleCopy}
    >
      {copied ? '✓' : 'Copy'}
    </button>
  );
}

function Chat() {
  const navigate = useNavigate();
  const location = useLocation();

  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [botTyping, setBotTyping] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [chatId, setChatId] = useState(null);
  const [profileMenuOpen, setProfileMenuOpen] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const [modalOpen, setModalOpen] = useState(false);

  const chatBodyRef = useRef(null);

  const urlChatId = new URLSearchParams(location.search).get('id');

  const fetchUserProfile = async (token) => {
    try {
      const response = await fetch('https://asknau-backend-20d79e207a54.herokuapp.com/profile', {
        method: 'GET',
        headers: { Authorization: `Bearer ${token}` },
      });
      if (response.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      }
    } catch {
      alert('Error fetching profile.');
    }
  };

  const fetchChatHistory = async (token) => {
    try {
      const response = await fetch('https://asknau-backend-20d79e207a54.herokuapp.com/history', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setChatHistory(data.history || []);
    } catch {
      alert('Failed to fetch chat history.');
    }
  };

  const fetchChatMessages = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`https://asknau-backend-20d79e207a54.herokuapp.com/chats/${id}/messages`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setMessages(data.messages.map((msg) => ({
        content: msg.user_message || msg.bot_message,
        isUser: !!msg.user_message,
      })));
    } catch {
      alert('Failed to load messages.');
    }
  };

  const sendMessage = async () => {
    if (!inputValue.trim() || botTyping) return;
    const message = inputValue.trim();
    setMessages((prev) => [...prev, { content: message, isUser: true }]);
    setInputValue('');

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('https://asknau-backend-20d79e207a54.herokuapp.com/ask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ question: message, chat_id: chatId }),
      });
      const data = await response.json();
      setMessages((prev) => [...prev, { content: data.answer, isUser: false }]);
    } catch {
      alert('Failed to send message.');
    }
  };

  const handleNewChatClick = () => {
    setMessages([]);
    setChatId(null);
    navigate('/chat');
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    fetchUserProfile(token);

    if (urlChatId) {
      fetchChatMessages(urlChatId);
    }
  }, [urlChatId, navigate]);

  return (
    <div className="app-container">
      <div className="chat-header">
        <span>AskNAU</span>
        <div>
          <button onClick={() => setProfileMenuOpen(!profileMenuOpen)}>Menu</button>
          {profileMenuOpen && (
            <div>
              <button onClick={handleNewChatClick}>New Chat</button>
              <button onClick={() => fetchChatHistory(localStorage.getItem('token'))}>
                History
              </button>
            </div>
          )}
        </div>
      </div>
      <div className="chat-body" ref={chatBodyRef}>
        {messages.map((msg, idx) => (
          <div key={idx} className={msg.isUser ? 'user-message' : 'bot-message'}>
            {msg.content}
          </div>
        ))}
      </div>
      <div className="chat-footer">
        <input
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Type your message"
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default Chat;
