// src/Chat.js
import React, { useState, useEffect, useRef, useCallback } from 'react';
import './Chat.css';
import { useNavigate, useLocation } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-regular-svg-icons';
import { faPlay, faRightFromBracket, faClockRotateLeft, faPlus } from '@fortawesome/free-solid-svg-icons';

function CopyButton({ text }) {
  // ... (No changes needed here)
}

function Chat() {
  const navigate = useNavigate();
  const location = useLocation();

  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [botTyping, setBotTyping] = useState(false);
  const [userScrolled, setUserScrolled] = useState(false);
  const [showGoDownButton, setShowGoDownButton] = useState(false);
  const [profileMenuOpen, setProfileMenuOpen] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [modalOpen, setModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const [userName, setUserName] = useState('');
  const [userEmail, setUserEmail] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [chatId, setChatId] = useState(null);

  const lastScrollTop = useRef(0);
  const chatBodyRef = useRef(null);
  const profileMenuRef = useRef(null);

  // Extract chat_id from URL
  const queryParams = new URLSearchParams(location.search);
  const urlChatId = queryParams.get('id');

  const scrollToBottom = useCallback(() => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTo({
        top: chatBodyRef.current.scrollHeight,
        behavior: 'smooth',
      });
      setUserScrolled(false);
    }
  }, []);

  const openModal = (content) => {
    setModalContent(content);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setModalContent('');
  };

  const fetchUserProfile = useCallback(async (token) => {
    try {
      const response = await fetch('https://asknau-backend-20d79e207a54.herokuapp.com/profile', {
        method: 'GET',
        headers: { Authorization: `Bearer ${token}` },
      });
      if (response.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
        return;
      }
      if (!response.ok) throw new Error(`Server error: ${response.statusText}`);
      const data = await response.json();
      setUserName(data.full_name);
      setUserEmail(data.email);
    } catch (error) {
      console.error('Failed to fetch user profile:', error);
    }
  }, [navigate]);

  const appendMessage = (content, isUser = true) => {
    if (isUser) {
      setMessages((prev) => [...prev, { content, isUser }]);
    } else {
      typeBotMessage(content);
    }
  };

  const typeBotMessage = (fullContent) => {
    setBotTyping(true);
    const words = fullContent.split(' ');
    let i = 0;
    const speed = 50;

    setMessages((prevMessages) => [...prevMessages, { content: '', isUser: false }]);
    const typingInterval = setInterval(() => {
      setMessages((prevMessages) => {
        const lastMessage = prevMessages[prevMessages.length - 1];
        const newContent = (lastMessage.content ? lastMessage.content + ' ' : '') + words[i];
        return [...prevMessages.slice(0, -1), { ...lastMessage, content: newContent }];
      });
      i++;
      if (i >= words.length) {
        clearInterval(typingInterval);
        setBotTyping(false);
      }
    }, speed);
  };

  const sendMessage = async (message = null) => {
    if (botTyping) return;
    const msg = message || inputValue.trim();
    if (!msg) return;
    if (msg.length > 2000) {
      setErrorMessage('Message cannot exceed 2000 characters.');
      return;
    }
    setErrorMessage('');
    appendMessage(msg, true);
    if (!message) setInputValue('');

    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const response = await fetch('https://asknau-backend-20d79e207a54.herokuapp.com/ask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ question: msg, chat_id: chatId }),
      });

      if (response.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
        return;
      }

      if (!response.ok) throw new Error(`Server error: ${response.statusText}`);

      const data = await response.json();
      if (data.error) {
        appendMessage(`Error: ${data.error}`, false);
      } else {
        setChatId(data.chat_id);
        typeBotMessage(data.answer);
      }
    } catch (error) {
      appendMessage(`Error: ${error.message}`, false);
    }
  };

  const handleChatBodyScroll = () => {
    const chatBody = chatBodyRef.current;
    const currentScrollTop = chatBody.scrollTop;
    const isAtBottom = Math.abs(chatBody.scrollHeight - chatBody.scrollTop - chatBody.clientHeight) < 1;

    if (currentScrollTop > lastScrollTop.current && !isAtBottom) {
      if (userScrolled) setShowGoDownButton(true);
    } else if (isAtBottom) {
      setShowGoDownButton(false);
      setUserScrolled(false);
    } else {
      setUserScrolled(true);
      setShowGoDownButton(false);
    }
    lastScrollTop.current = currentScrollTop;
  };

  const toggleProfileMenu = () => {
    setProfileMenuOpen(!profileMenuOpen);
  };

  const fetchHistory = async (token) => {
    try {
      const response = await fetch('https://asknau-backend-20d79e207a54.herokuapp.com/history', {
        method: 'GET',
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!response.ok) {
        console.error('Failed to fetch history');
        return;
      }
      const data = await response.json();
      setChatHistory(data.history || []);
    } catch (err) {
      console.error('Error fetching history:', err);
    }
  };

  const handleHistoryClick = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    await fetchHistory(token);
    openModal('History');
    setProfileMenuOpen(false);
  };

  const handleNewChatClick = (e) => {
    e.preventDefault();
    // Reset chat state for a new conversation
    setChatId(null);
    setMessages([]);
    setInputValue('');
    navigate('/chat'); // Optionally reset the URL
  };

  const loadSelectedHistory = async (selectedChatId) => {
    setModalOpen(false);
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    // Directly set the chat ID and load messages
    setChatId(selectedChatId);
    await loadChatMessages(selectedChatId);
  };

  const loadChatMessages = useCallback(async (id) => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    // Clear current messages before loading new ones
    setMessages([]);
    setBotTyping(false);
    setUserScrolled(false);
    setShowGoDownButton(false);

    try {
      const response = await fetch(`https://asknau-backend-20d79e207a54.herokuapp.com/chats/${id}/messages`, {
        method: 'GET',
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
        return;
      }

      if (!response.ok) throw new Error('Failed to fetch chat messages');

      const data = await response.json();
      const loadedMessages = data.messages.map((m) => {
        if (m.user_message) {
          return { content: m.user_message, isUser: true };
        } else if (m.bot_message) {
          return { content: m.bot_message, isUser: false };
        }
        return null;
      }).filter(Boolean);

      setChatId(id);
      setMessages(loadedMessages);
      // Optionally scroll to bottom after loading
      scrollToBottom();
    } catch (err) {
      console.error('Error fetching chat messages:', err);
    }
  }, [navigate, scrollToBottom]);

  const sendWelcomeMessage = useCallback(() => {
    if (messages.length === 0) {
      appendMessage('Hello! Welcome to AskNAU. How can I assist you today?', false);
    }
  }, [messages]);

  // Handle profile menu outside click
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        profileMenuRef.current &&
        !profileMenuRef.current.contains(event.target) &&
        event.target.nodeName !== 'IMG'
      ) {
        setProfileMenuOpen(false);
      }
    };

    if (profileMenuOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [profileMenuOpen]);

  // On initial load and when chatId or urlChatId changes
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    } else {
      fetchUserProfile(token).then(() => {
        if (chatId) {
          loadChatMessages(chatId);
        } else if (urlChatId) {
          loadChatMessages(urlChatId);
        } else {
          sendWelcomeMessage();
        }
      });
    }
  }, [navigate, fetchUserProfile, chatId, urlChatId, loadChatMessages, sendWelcomeMessage]);

  // Scroll down when messages update if user hasn't scrolled away
  useEffect(() => {
    if (!userScrolled) scrollToBottom();
  }, [messages, userScrolled, scrollToBottom]);

  return (
    <div className="app-container text-gray-300 flex items-center justify-center h-full">
      <div className="w-full h-full flex flex-col max-w-4xl mx-auto">
        <div className="chat-header p-4 flex items-center justify-between">
          <div className="flex items-center">
            <span className="text-2xl font-bold">AskNAU</span>
            <span style={{ color: 'rgba(6,147,227,1)' }} className="text-x font-normal ml-2">
              (North American University AI)
            </span>
          </div>
          <div className="relative">
            <img
              src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png"
              alt="User profile"
              className="w-8 h-8 rounded-full cursor-pointer"
              onClick={toggleProfileMenu}
            />
            {profileMenuOpen && (
              <div id="profile-menu" ref={profileMenuRef} className="absolute right-0 mt-2 w-48 rounded-md shadow-lg bg-gray-800">
                <button
                  className="block px-4 py-2 text-left w-full arkasy hover:bg-gray-700"
                  onClick={(e) => {
                    e.preventDefault();
                    openModal('Profile');
                    setProfileMenuOpen(false);
                  }}
                >
                  <FontAwesomeIcon icon={faUser} style={{ marginRight: '10px' }} />
                  Profile
                </button>
                <button className="block px-4 py-2 text-left w-full arkasy hover:bg-gray-700" onClick={handleNewChatClick}>
                  <FontAwesomeIcon icon={faPlus} style={{ marginRight: '10px' }} />
                  New Chat
                </button>
                <button className="block px-4 py-2 text-left w-full arkasy hover:bg-gray-700" onClick={handleHistoryClick}>
                  <FontAwesomeIcon icon={faClockRotateLeft} style={{ marginRight: '10px' }} />
                  History
                </button>
                <button
                  className="block px-4 py-2 text-left w-full arkasy hover:bg-gray-700"
                  onClick={(e) => {
                    e.preventDefault();
                    navigate('/demo');
                    setProfileMenuOpen(false);
                  }}
                >
                  <FontAwesomeIcon icon={faPlay} style={{ marginRight: '10px' }} />
                  Demo
                </button>
                <button
                  className="block px-4 py-2 text-left w-full arkasy hover:bg-gray-700"
                  onClick={(e) => {
                    e.preventDefault();
                    localStorage.removeItem('token');
                    navigate('/login');
                    setProfileMenuOpen(false);
                  }}
                >
                  <FontAwesomeIcon icon={faRightFromBracket} style={{ marginRight: '10px' }} />
                  Logout
                </button>
              </div>
            )}
          </div>
        </div>
        <div className="chat-body flex flex-col p-4 space-y-4 overflow-y-auto" ref={chatBodyRef} onScroll={handleChatBodyScroll}>
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.isUser ? 'user-message' : 'bot-message'}`}>
              {message.isUser ? (
                message.content
              ) : (
                <div className="message-wrapper flex items-center justify-between">
                  <div className="message-content">{message.content}</div>
                  <CopyButton text={message.content} />
                </div>
              )}
            </div>
          ))}
        </div>
        <div className="chat-footer p-4 flex flex-col items-center space-y-2">
          {errorMessage && <div className="error-message text-red-600 text-sm">{errorMessage}</div>}
          <div className="input-container flex-1 w-full flex items-center">
            <input
              type="text"
              value={inputValue}
              onChange={(e) => {
                if (e.target.value.length <= 2000) {
                  setInputValue(e.target.value);
                  setErrorMessage('');
                } else {
                  setErrorMessage('Message cannot exceed 2000 characters.');
                }
              }}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  e.preventDefault();
                  sendMessage();
                }
              }}
              placeholder="Message AskNAU..."
              autoComplete="off"
              className="flex-grow p-2 rounded-l-md bg-gray-800 text-white"
            />
            <button onClick={() => sendMessage()} className="p-2 bg-blue-600 rounded-r-md">
              <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7"></path>
              </svg>
            </button>
          </div>
          <div className="disclaimer">@ AskNAU - 2024</div>
        </div>
        {showGoDownButton && (
          <button className="go-down-button" onClick={scrollToBottom}>
            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
            </svg>
          </button>
        )}
      </div>
      {modalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-box" onClick={(e) => e.stopPropagation()}>
            <h2 className="modal-title font-bold">Chat</h2>
            {modalContent === 'Profile' && (
              <div className="space-y-4 modal-content">
                <p>
                  <span className="font-semibold">Name:</span> {userName}
                </p>
                <p>
                  <span className="font-semibold">Email:</span> {userEmail}
                </p>
                <div className="disclaimer">For profile questions, contact zkalykov@na.edu</div>
              </div>
            )}
            {modalContent === 'History' && (
              <div className="space-y-4 modal-content">
                {chatHistory && chatHistory.length > 0 ? (
                  chatHistory.map((h, idx) => (
                    <div
                      key={idx}
                      className="arkasy p-2 rounded cursor-pointer hover:bg-gray-700"
                      onClick={() => loadSelectedHistory(h.chat_id)}
                    >
                      <div className="flex justify-between items-center">
                        <span className="font-semibold">{h.history_title}</span>
                        <span className="text-sm text-gray-400">
                          {h.date_created ? new Date(h.date_created).toLocaleString() : ''}
                        </span>
                      </div>
                      {h.title && <div className="text-sm text-gray-500">- {h.title}</div>}
                    </div>
                  ))
                ) : (
                  <div>No history found.</div>
                )}
              </div>
            )}
            <button onClick={closeModal} className="modal-close-button mt-4 px-4 py-2 bg-red-600 text-white rounded">
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Chat;
