// src/Chat.js

import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

function CopyButton({ text }) {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    if (!navigator.clipboard) {
      const textArea = document.createElement('textarea');
      textArea.value = text;
      document.body.appendChild(textArea);
      textArea.focus();
      textArea.select();
      try {
        document.execCommand('copy');
        setCopied(true);
        setTimeout(() => setCopied(false), 1000);
      } catch (err) {
        alert('Failed to copy text.');
      }
      document.body.removeChild(textArea);
      return;
    }

    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 1000);
    } catch (error) {
      alert('Failed to copy text. Your browser may not support this feature.');
    }
  };

  return (
    <button
      className="copy-button ml-2 mt-2 text-gray-400 hover:text-blue-500"
      aria-label={copied ? 'Text copied' : 'Copy text'}
      data-tooltip={copied ? 'Copied' : 'Copy'}
      onClick={handleCopy}
    >
      {copied ? (
        <svg fill="currentColor" viewBox="0 0 20 20" className="w-4 h-4">
          <path d="M16.707 5.293a1 1 0 00-1.414 0L7.5 13.086l-2.793-2.793a1 1 0 00-1.414 1.414l3.5 3.5a1 1 0 001.414 0l8-8a1 1 0 000-1.414z"></path>
        </svg>
      ) : (
        <svg fill="currentColor" viewBox="0 0 20 20" className="w-4 h-4">
          <path d="M8 2a2 2 0 00-2 2v2h2V4h8v8h-2v2h2a 2 2 0 002-2V4a 2 2 0 00-2-2H8z"></path>
          <path d="M2 8a2 2 0 012-2h8a 2 2 0 012 2v8a 2 2 0 01-2 2H4a 2 2 0 01-2-2V8z"></path>
        </svg>
      )}
    </button>
  );
}

function Chat() {
  // ... [Rest of your state and hooks]

  // Ensure you import useNavigate from react-router-dom
  const navigate = useNavigate();

  // ... [Rest of your functions]

  return (
    <div className="flex flex-col h-screen bg-gray-900 text-white">
      {/* Chat Header */}
      <div className="chat-header p-4 flex items-center justify-between border-b border-gray-700">
        <div className="flex items-center">
          <span className="text-2xl font-bold">AskNAU</span>
          <span
            style={{ color: 'rgba(6,147,227,1)' }}
            className="text-sm font-normal ml-2"
          >
            (North American University AI)
          </span>
        </div>
        {/* Profile Picture and Menu */}
        <div className="relative">
          <img
            src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png"
            alt="User profile"
            className="w-8 h-8 rounded-full cursor-pointer"
            onClick={toggleProfileMenu}
          />
          {profileMenuOpen && (
            <div
              id="profile-menu"
              ref={profileMenuRef}
              className="absolute right-0 mt-2 w-48 rounded-md shadow-lg bg-gray-800 z-10"
            >
              <button
                className="block px-4 py-2 hover:bg-gray-700 w-full text-left"
                onClick={() => {
                  openModal('Profile');
                  setProfileMenuOpen(false);
                }}
              >
                Profile
              </button>
              <button
                className="block px-4 py-2 hover:bg-gray-700 w-full text-left"
                onClick={() => {
                  openModal('More');
                  setProfileMenuOpen(false);
                }}
              >
                More
              </button>
              <button
                className="block px-4 py-2 hover:bg-gray-700 w-full text-left"
                onClick={() => {
                  // Handle logout
                  localStorage.removeItem('token');
                  navigate('/login');
                  setProfileMenuOpen(false);
                }}
              >
                Logout
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Chat Body */}
      <div
        className="chat-body flex flex-col p-4 space-y-4 overflow-y-auto flex-grow"
        ref={chatBodyRef}
        onScroll={handleChatBodyScroll}
      >
        {messages.map((message, index) => (
          <div
            key={index}
            className={`message ${
              message.isUser
                ? 'self-end bg-blue-600 text-white'
                : 'self-start bg-gray-700 text-white'
            } p-3 rounded-lg max-w-xs md:max-w-md`}
          >
            {message.isUser ? (
              message.content
            ) : (
              <div className="flex items-start">
                <div className="flex-1">{message.content}</div>
                <CopyButton text={message.content} />
              </div>
            )}
          </div>
        ))}
        {botTyping && (
          <div className="self-start bg-gray-700 text-white p-3 rounded-lg max-w-xs md:max-w-md">
            Typing...
          </div>
        )}
      </div>

      {/* Chat Footer */}
      <div className="chat-footer p-4 flex flex-col items-center space-y-2 border-t border-gray-700">
        {errorMessage && (
          <div className="error-message text-red-500 text-sm">
            {errorMessage}
          </div>
        )}
        <div className="input-container w-full flex items-center">
          <input
            type="text"
            value={inputValue}
            onChange={(e) => {
              if (e.target.value.length <= 2000) {
                setInputValue(e.target.value);
                setErrorMessage('');
              } else {
                setErrorMessage('Message cannot exceed 2000 characters.');
              }
            }}
            onKeyDown={(e) => {
              if (e.key === 'Enter') {
                e.preventDefault();
                sendMessage();
              }
            }}
            placeholder="Type your message..."
            autoComplete="off"
            className="flex-grow p-2 rounded-l-md bg-gray-800 text-white focus:outline-none"
          />
          <button
            onClick={() => sendMessage()}
            className="p-2 bg-blue-600 hover:bg-blue-700 rounded-r-md"
          >
            {/* Send Icon */}
            <svg
              className="w-6 h-6 text-white"
              fill="currentColor"
              viewBox="0 0 20 20"
            >
              <path
                fillRule="evenodd"
                d="M10.293 15.293a1 1 0 010-1.414L13.586 10 10.293 6.707a1 1 0 011.414-1.414L16.414 10l-4.707 4.707a1 1 0 01-1.414 0z"
                clipRule="evenodd"
              />
            </svg>
          </button>
        </div>
        {/* Disclaimer */}
        <div className="text-sm text-gray-500">@ AskNAU - 2024</div>
      </div>

      {/* Scroll to Bottom Button */}
      {showGoDownButton && (
        <button
          className="go-down-button fixed bottom-20 right-4 p-2 bg-blue-600 hover:bg-blue-700 rounded-full"
          onClick={scrollToBottom}
        >
          <svg
            className="w-6 h-6 text-white"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M19 9l-7 7-7-7"
            ></path>
          </svg>
        </button>
      )}

      {/* Modal */}
      {modalOpen && (
        <div
          className="modal-overlay fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center"
          onClick={closeModal}
        >
          <div
            className="modal-box bg-gray-800 p-6 rounded-lg text-white max-w-md mx-auto"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="modal-title font-bold text-xl mb-4">
              {modalContent}
            </h2>
            {/* Content based on modalContent */}
            {modalContent === 'Profile' && (
              <div className="space-y-4">
                <p>
                  <span className="font-semibold">AskNAU</span> is your AI for
                  answering North American university questions, limited to{' '}
                  <span className="font-semibold">10 daily queries</span> per
                  user for fair access.
                </p>
              </div>
            )}

            {modalContent === 'More' && (
              <div className="space-y-4">
                <p>
                  AskNAU is a specialized AI model designed to assist students
                  at North American universities by answering their questions
                  accurately and efficiently. This fine-tuned system is crafted
                  to handle a wide range of academic and campus-related queries
                  with contextual relevance and reliability.
                </p>

                <p>
                  The development team includes
                  <span className="underline">
                    {' '}
                    Software Engineer Zhyrgalbek Kalykov
                  </span>
                  ,
                  <span className="underline">
                    {' '}
                    Supervisor Sabina Adhikari
                  </span>
                  , and
                  <span className="underline">
                    {' '}
                    Designer Ayana Ibragim Kyzy
                  </span>
                  , who have worked together to ensure AskNAU’s technical
                  precision and user-friendly experience.
                </p>

                <h3
                  className="font-bold text-blue-500"
                >
                  Policies
                </h3>

                <p>
                  AskNAU tracks user activity to monitor usage, allowing up to
                  10 daily messages per user. This ensures fair access and
                  optimal performance for all.
                </p>
              </div>
            )}

            <button
              onClick={closeModal}
              className="modal-close-button mt-4 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Chat;
