// src/Chat.js

import React, { useState, useEffect, useRef, useCallback } from 'react';
import './Chat.css';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-regular-svg-icons';
import { faPlay, faRightFromBracket } from '@fortawesome/free-solid-svg-icons';

/**
 * CopyButton Component
 */
function CopyButton({ text }) {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    if (!navigator.clipboard) {
      const textArea = document.createElement('textarea');
      textArea.value = text;
      document.body.appendChild(textArea);
      textArea.focus();
      textArea.select();
      try {
        document.execCommand('copy');
        setCopied(true);
        setTimeout(() => setCopied(false), 1000);
      } catch (err) {
        alert('Failed to copy text.');
      }
      document.body.removeChild(textArea);
      return;
    }

    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 1000);
    } catch (error) {
      alert('Failed to copy text. Your browser may not support this feature.');
    }
  };

  return (
    <button
      className="copy-button"
      aria-label={copied ? 'Text copied' : 'Copy text'}
      data-tooltip={copied ? 'Copied' : 'Copy'}
      onClick={handleCopy}
    >
      {copied ? (
        <svg fill="currentColor" viewBox="0 0 20 20">
          <path d="M16.707 5.293a1 1 0 00-1.414 0L7.5 13.086l-2.793-2.793a1 1 0 00-1.414 1.414l3.5 3.5a1 1 0 001.414 0l8-8a1 1 0 000-1.414z"></path>
        </svg>
      ) : (
        <svg fill="currentColor" viewBox="0 0 20 20">
          <path d="M8 2a2 2 0 00-2 2v2h2V4h8v8h-2v2h2a 2 2 0 002-2V4a 2 2 0 00-2-2H8z"></path>
          <path d="M2 8a2 2 0 012-2h8a 2 2 0 012 2v8a 2 2 0 01-2 2H4a 2 2 0 01-2-2V8z"></path>
        </svg>
      )}
    </button>
  );
}

function Chat() {
  const navigate = useNavigate();
  const backendUrl = 'https://asknau-backend-20d79e207a54.herokuapp.com';

  // State variables
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [botTyping, setBotTyping] = useState(false);
  const [userScrolled, setUserScrolled] = useState(false);
  const [showGoDownButton, setShowGoDownButton] = useState(false);
  const [profileMenuOpen, setProfileMenuOpen] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [modalOpen, setModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState('');

  // User profile
  const [userName, setUserName] = useState('');
  const [userEmail, setUserEmail] = useState('');

  // For history and chats
  const [historyModalOpen, setHistoryModalOpen] = useState(false);
  const [history, setHistory] = useState([]);
  const [chatId, setChatId] = useState(null);

  // Refs
  const chatBodyRef = useRef(null);
  const lastScrollTop = useRef(0);
  const profileMenuRef = useRef(null);

  const openModal = (content) => {
    setModalContent(content);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setModalContent('');
  };

  const openHistoryModal = () => setHistoryModalOpen(true);
  const closeHistoryModal = () => setHistoryModalOpen(false);

  const fetchUserProfile = useCallback(async (token) => {
    try {
      const response = await fetch(`${backendUrl}/profile`, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
        return;
      }

      if (!response.ok) {
        throw new Error(`Server error: ${response.statusText}`);
      }

      const data = await response.json();
      setUserName(data.full_name);
      setUserEmail(data.email);
    } catch (error) {
      console.error('Failed to fetch user profile:', error);
    }
  }, [navigate, backendUrl]);

  const sendWelcomeMessage = useCallback(() => {
    const welcomeMessage = 'Hello! Welcome to AskNAU. How can I assist you today?';
    setMessages([{ content: welcomeMessage, isUser: false }]);
  }, []);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    } else {
      sendWelcomeMessage();
      fetchUserProfile(token);
      fetchHistoryData(token);
    }
  }, [navigate, sendWelcomeMessage, fetchUserProfile]);

  useEffect(() => {
    if (!userScrolled) {
      scrollToBottom();
    }
  }, [messages, userScrolled]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        profileMenuRef.current &&
        !profileMenuRef.current.contains(event.target) &&
        event.target.nodeName !== 'IMG'
      ) {
        setProfileMenuOpen(false);
      }
    };

    if (profileMenuOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [profileMenuOpen]);

  const appendMessage = (content, isUser = true) => {
    if (isUser) {
      setMessages((prev) => [...prev, { content, isUser }]);
    } else {
      typeBotMessage(content);
    }
  };

  const typeBotMessage = (fullContent) => {
    setBotTyping(true);
    const words = fullContent.split(' ');
    let i = 0;
    const speed = 50;

    setMessages((prevMessages) => [...prevMessages, { content: '', isUser: false }]);

    const typingInterval = setInterval(() => {
      if (i < words.length) {
        setMessages((prevMessages) => {
          const lastMessage = prevMessages[prevMessages.length - 1];
          const newContent = (lastMessage.content ? lastMessage.content + ' ' : '') + words[i];
          return [...prevMessages.slice(0, -1), { ...lastMessage, content: newContent }];
        });
        i++;
      } else {
        clearInterval(typingInterval);
        setBotTyping(false);
      }
    }, speed);
  };

  const sendMessage = async () => {
    if (botTyping) return;

    const msg = inputValue.trim();
    if (msg === '') return;

    if (msg.length > 2000) {
      setErrorMessage('Message cannot exceed 2000 characters.');
      return;
    } else {
      setErrorMessage('');
    }

    appendMessage(msg, true);
    setInputValue('');
    setBotTyping(true);

    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const response = await fetch(`${backendUrl}/ask`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ question: msg, chat_id: chatId }),
      });

      if (response.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
        return;
      }

      if (!response.ok) {
        throw new Error(`Server error: ${response.statusText}`);
      }

      const data = await response.json();

      if (data.error) {
        appendMessage(`Error: ${data.error}`, false);
      } else {
        setChatId(data.chat_id);
        typeBotMessage(data.answer);
      }
    } catch (error) {
      appendMessage(`Error: ${error.message}`, false);
    }
  };

  const scrollToBottom = () => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTo({
        top: chatBodyRef.current.scrollHeight,
        behavior: 'smooth',
      });
      setUserScrolled(false);
    }
  };

  const handleChatBodyScroll = () => {
    const chatBody = chatBodyRef.current;
    const currentScrollTop = chatBody.scrollTop;
    const isAtBottom = Math.abs(
      chatBody.scrollHeight - chatBody.scrollTop - chatBody.clientHeight
    ) < 1;

    if (currentScrollTop > lastScrollTop.current && !isAtBottom) {
      if (userScrolled) setShowGoDownButton(true);
    } else if (isAtBottom) {
      setShowGoDownButton(false);
      setUserScrolled(false);
    } else {
      setUserScrolled(true);
      setShowGoDownButton(false);
    }
    lastScrollTop.current = currentScrollTop;
  };

  const toggleProfileMenu = () => {
    setProfileMenuOpen(!profileMenuOpen);
  };

  const fetchHistoryData = async (token) => {
    try {
      const response = await fetch(`${backendUrl}/history`, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
        return;
      }

      if (!response.ok) {
        throw new Error('Failed to fetch history.');
      }

      const data = await response.json();
      setHistory(data.history);
    } catch (error) {
      console.error('Failed to fetch chat history:', error);
    }
  };

  const loadChatFromHistory = async (selectedChatId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const response = await fetch(`${backendUrl}/chats/${selectedChatId}/messages`, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
        return;
      }

      if (!response.ok) {
        throw new Error('Failed to load chat messages.');
      }

      const data = await response.json();
      setMessages(data.messages || []);
      setChatId(selectedChatId);
      closeHistoryModal();
    } catch (error) {
      console.error('Error loading chat messages:', error);
    }
  };

  const startNewChat = () => {
    setChatId(null);
    setMessages([]);
    sendWelcomeMessage();
  };

  return (
    <div className="app-container text-gray-300 flex items-center justify-center h-full">
      <div className="w-full h-full flex flex-col max-w-4xl mx-auto">
        {/* Chat Header */}
        <div className="chat-header p-4 flex items-center justify-between">
          <div className="flex items-center">
            <span className="text-2xl font-bold">AskNAU</span>
            <span
              style={{ color: 'rgba(6,147,227,1)' }}
              className="text-x font-normal ml-2"
            >
              (North American University AI)
            </span>
          </div>

          <div className="flex items-center space-x-4">
            {/* New Chat Button */}
            <button
              className="bg-blue-600 px-3 py-1 rounded"
              onClick={startNewChat}
            >
              New Chat
            </button>
            {/* History Button */}
            <button
              className="bg-gray-700 px-3 py-1 rounded"
              onClick={openHistoryModal}
            >
              History
            </button>

            {/* Profile Menu */}
            <div className="relative">
              <img
                src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png"
                alt="User profile"
                className="w-8 h-8 rounded-full cursor-pointer"
                onClick={toggleProfileMenu}
              />

              {profileMenuOpen && (
                <div
                  id="profile-menu"
                  ref={profileMenuRef}
                  className="absolute right-0 mt-2 w-48 rounded-md shadow-lg"
                  style={{
                    backgroundColor: '#1a1a1a',
                    border: '1px solid #333',
                    zIndex: 100,
                  }}
                >
                  <button
                    className="block px-4 py-2 text-left w-full"
                    onClick={(e) => {
                      e.preventDefault();
                      openModal('Profile');
                      setProfileMenuOpen(false);
                    }}
                  >
                    <FontAwesomeIcon
                      icon={faUser}
                      style={{ marginRight: '10px' }}
                    />
                    Profile
                  </button>
                  <button
                    className="block px-4 py-2 text-left w-full"
                    onClick={(e) => {
                      e.preventDefault();
                      navigate('/demo'); 
                      setProfileMenuOpen(false);
                    }}
                  >
                    <FontAwesomeIcon
                      icon={faPlay}
                      style={{ marginRight: '10px' }}
                    />
                    Demo
                  </button>
                  <button
                    className="block px-4 py-2 text-left w-full"
                    onClick={(e) => {
                      e.preventDefault();
                      localStorage.removeItem('token');
                      navigate('/login');
                      setProfileMenuOpen(false);
                    }}
                  >
                    <FontAwesomeIcon
                      icon={faRightFromBracket}
                      style={{ marginRight: '10px' }}
                    />
                    Logout
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Chat Body */}
        <div
          className="chat-body flex flex-col p-4 space-y-4 overflow-y-auto"
          ref={chatBodyRef}
          onScroll={handleChatBodyScroll}
          style={{ flex: 1 }}
        >
          {messages.map((message, index) => (
            <div
              key={index}
              className={`message ${
                message.isUser ? 'user-message' : 'bot-message'
              }`}
            >
              {message.isUser ? (
                <div>{message.content}</div>
              ) : (
                <div className="message-wrapper">
                  <div className="message-content">{message.content}</div>
                  <CopyButton text={message.content} />
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Chat Footer */}
        <div className="chat-footer p-4 flex flex-col items-center space-y-2">
          {errorMessage && (
            <div className="error-message text-red-600 text-sm">
              {errorMessage}
            </div>
          )}
          <div className="input-container flex-1 w-full flex items-center">
            <input
              type="text"
              value={inputValue}
              onChange={(e) => {
                if (e.target.value.length <= 2000) {
                  setInputValue(e.target.value);
                  setErrorMessage('');
                } else {
                  setErrorMessage('Message cannot exceed 2000 characters.');
                }
              }}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  e.preventDefault();
                  sendMessage();
                }
              }}
              placeholder="Message AskNAU..."
              autoComplete="off"
              className="flex-grow p-2 rounded-l-md bg-gray-800 text-white"
            />
            <button
              onClick={sendMessage}
              className="p-2 bg-blue-600 rounded-r-md"
            >
              <svg
                className="w-6 h-6 text-white"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7" />
              </svg>
            </button>
          </div>
          <div className="disclaimer">@ AskNAU - 2024</div>
        </div>

        {showGoDownButton && (
          <button className="go-down-button" onClick={scrollToBottom}>
            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M19 9l-7 7-7-7"
              ></path>
            </svg>
          </button>
        )}
      </div>

      {/* Profile Modal */}
      {modalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-box" onClick={(e) => e.stopPropagation()}>
            <h2 className="modal-title font-bold">{modalContent}</h2>
            {modalContent === 'Profile' && (
              <div className="space-y-4">
                <p>
                  <span className="font-semibold">Name:</span> {userName}
                </p>
                <p>
                  <span className="font-semibold">Email:</span> {userEmail}
                </p>
              </div>
            )}
            <button onClick={closeModal} className="modal-close-button">
              Close
            </button>
          </div>
        </div>
      )}

      {/* History Modal */}
      {historyModalOpen && (
        <div className="modal-overlay" onClick={closeHistoryModal}>
          <div className="modal-box" onClick={(e) => e.stopPropagation()}>
            <h2 className="modal-title font-bold">Conversation History</h2>
            <div className="space-y-2">
              {history.map((h) => (
                <div
                  key={h.chat_id}
                  className="history-item p-2 bg-gray-800 rounded cursor-pointer hover:bg-gray-700"
                  onClick={() => loadChatFromHistory(h.chat_id)}
                >
                  {h.history_title} - {h.date_created ? new Date(h.date_created).toLocaleString() : ''}
                </div>
              ))}
              {history.length === 0 && <p>No conversations found.</p>}
            </div>
            <button onClick={closeHistoryModal} className="modal-close-button mt-4">
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Chat;
