// src/Login.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function Login() {
  // ... [existing code remains unchanged]

  return (
    <div className="flex items-center justify-center min-h-screen bg-[#0e0e0e]">
      <div className="w-full max-w-md p-6 bg-[#424242] rounded-md shadow-md">
        <h2 className="text-2xl font-semibold text-center text-white mb-6">
          Sign In
        </h2>
        <form onSubmit={handleLoginSubmit}>
          {errorMessage && (
            <div className="mb-4 text-[#ff4d4d] text-center">
              {errorMessage}
            </div>
          )}
          <div className="mb-4">
            <input
              type="email"
              placeholder="Email"
              value={loginEmail}
              onChange={(e) => setLoginEmail(e.target.value)}
              required
              className="w-full px-4 py-2 rounded bg-[#4b4b4b] border border-[#444] text-white placeholder-gray-400 focus:outline-none"
            />
          </div>
          <div className="mb-6">
            <input
              type="password"
              placeholder="Password"
              value={loginPassword}
              onChange={(e) => setLoginPassword(e.target.value)}
              required
              className="w-full px-4 py-2 rounded bg-[#4b4b4b] border border-[#444] text-white placeholder-gray-400 focus:outline-none"
            />
          </div>
          <button
            type="submit"
            className="w-full py-2 mb-4 bg-[#0e0e0e] hover:bg-[#333] hover:text-[rgba(6,147,227,1)] border border-[#444] rounded text-white font-semibold transition-colors"
          >
            Login
          </button>
          <p className="text-center text-[#aaa]">
            Don't have an account?{' '}
            <Link
              to="/register"
              className="text-[rgba(6,147,227,1)] hover:underline cursor-pointer"
            >
              Register
            </Link>
          </p>
        </form>
      </div>
    </div>
  );
}

export default Login;
